// Create the TaskCard component here
import React from "react";
import { Draggable } from "react-beautiful-dnd";
import { useSelector } from "react-redux";
import Card from "@mui/material/Card";
import SubjectIcon from "@mui/icons-material/Subject";
import EditIcon from "@mui/icons-material/Edit";

function TaskCard({ taskId, index }) {
  const data = useSelector((state) => state.task);
  const { taskTitle, taskDescription } = data.tasks[taskId];

  return (
    <>
      <Draggable draggableId={taskId} index={index}>
        {(provided) => (
          <div
            ref={provided.innerRef}
            {...provided.droppableProps}
            {...provided.dragHandleProps}
          >
            <Card className="task-card">
              <div className="task-title">{taskTitle}</div>
              <div className="task-description">{taskDescription}</div>
            </Card>
          </div>
        )}
      </Draggable>
    </>
  );
}

export default TaskCard;
