// Create the TaskCard component here
import React from "react";
import { Draggable } from "react-beautiful-dnd";
import { useSelector } from "react-redux";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import SubjectIcon from "@mui/icons-material/Subject";
import Tooltip from "@mui/material/Tooltip";
import EditIcon from "@mui/icons-material/Edit";
import { useDispatch } from "react-redux";

function TaskCard({ currTaskColId, task, index }) {
  const data = useSelector((state) => state.task);
  const { taskId, taskTitle, taskDescription } = task;

  const dispatch = useDispatch();

  return (
    <>
      <Draggable draggableId={taskId} index={index}>
        {(provided) => (
          <div
            ref={provided.innerRef}
            {...provided.droppableProps}
            {...provided.dragHandleProps}
          >
            <Card className="task-card">
              <CardContent>
                <div className="task-title">{taskTitle}</div>
                <div className="task-description">{taskDescription}</div>
              </CardContent>
              <div className="task-icons">
                <Tooltip title="Description">
                  <SubjectIcon className="task-icon" />
                </Tooltip>
                <Tooltip title="Edit">
                  <EditIcon className="task-icon" />
                </Tooltip>
              </div>
            </Card>
          </div>
        )}
      </Draggable>
    </>
  );
}

export default TaskCard;
